package Ejemplos_For;

import java.util.*;

public class Ejemplos_For0 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		Scanner sn = new Scanner(System.in);
		// Ejemplo1: Que diga pedrito 10 veces
		// "int i = 0" es la declaracion de valor, define cuanto es i
		// "i < 10" es la condicion, esto es lo que determinara cuando se acaba el bucle for
		// "i++" es para hacer el incremento de i y asi poder terminar el bucle for
		for (int i = 0; i < 10; i++) {
			System.out.println("Pedrito");
		}
	}

}
